import React, { Component } from "react";
import { Form, Dropdown } from "semantic-ui-react";

const options = [
  { key: "f", text: "Fitness", value: "fitness" },
  { key: "s", text: "Self Love", value: "self love" },
  { key: "p", text: "Positive Affirmation", value: "positive affirmation" },
  { key: "i", text: "Inspirational", value: "inspirational" }
];

const Signup = () => {
//   const { value } = this.state;
  return (
    <Form>
      <Form.Group widths="equal">
        <Form.Input fluid label="Username" placeholder="Username" />
        <Form.Input fluid label="Email" placeholder="Email" />
      </Form.Group>
      <Form.Group widths="equal">
        <Form.Input fluid label="Password" placeholder="Password" />
        <Form.Input fluid label="Confirm Password" placeholder="Email" />
      </Form.Group>
      <Form.Group inline />
      <Form.Group inline>
        <label>Weight Unit</label>
        <Form.Radio
          label="Lbs."
          value="lb"
        //   checked={value === "lb"}
          onChange={this.handleChange}
        />
        <Form.Radio
          label="Kgs."
          value="kg"
        //   checked={value === "kg"}
          onChange={this.handleChange}
        />
        <Dropdown
          placeholder="Quote Preference"
          fluid
          multiple
          selection
          options={options}
        />
      </Form.Group>
      <Form.Button>Submit</Form.Button>
    </Form>
  );
};

export default Signup;
