import React from "react";
import { Button, Header, Icon, Modal, Form, Image} from "semantic-ui-react";
import {Link} from "react-router-dom"

const WeightForm = () => {
  return (

      <Modal trigger={<Button circular color="blue" icon="plus"></Button>} size="small" centered={false}>
      <Modal.Header>Add Today's Weight</Modal.Header>
    <Modal.Content image>
      <Modal.Description>
      <Form>
      <Form.Field label='Weight' control='input' type='number' />
      <Form.Field>
          <Button type="submit">Submit</Button>
      </Form.Field>
      </Form>
      </Modal.Description>
    </Modal.Content>
      </Modal>

  );
};

export default WeightForm;
