import React, { Component } from "react";
import { Form, Dropdown, Button, Grid } from "semantic-ui-react";

const options = [
  { key: "f", text: "Fitness", value: "fitness" },
  { key: "s", text: "Self Love", value: "self love" },
  { key: "p", text: "Positive Affirmation", value: "positive affirmation" },
  { key: "i", text: "Inspirational", value: "inspirational" }
];

const Signup = () => {
  //   const { value } = this.state;

  const handleChange = event => {};

  return (
    <Grid centered columns="twelve">
      <Grid.Row>
        <Grid.Column width="eight">
          <Form>
            <Form.Input fluid label="Username" placeholder="Username" />
            <Form.Input fluid label="Email" placeholder="Email" />

            <Form.Input
              fluid
              label="Password"
              placeholder="Password"
              type="password"
            />
            <Form.Input
              fluid
              label="Confirm Password"
              placeholder="Confirm Password"
              type="password"
            />

            <Form.Group inline>
              <label>Weight Unit</label>
              <Form.Radio
                label="Lbs."
                value="lb"
                //   checked={value === "lb"}
                onChange={handleChange}
              />
              <Form.Radio
                label="Kgs."
                value="kg"
                //   checked={value === "kg"}
                onChange={handleChange}
              />
            </Form.Group>

            <Dropdown
              placeholder="Quote Preference"
              fluid
              multiple
              selection
              options={options}
            />

            <br />
            <Button animated="fade" color="purple">
              <Button.Content visible> Sign Up </Button.Content>
              <Button.Content hidden> Love Yourself </Button.Content>
            </Button>
          </Form>
        </Grid.Column>
      </Grid.Row>
    </Grid>
  );
};

export default Signup;
