    //   placeholder="Confirm Password"
    //   type="password"
    // />
    // <Form.Group grouped>
    //   <label>Weight Unit</label>
    //   <Form.Group inline>
    //     <Form.Radio
    //       label="Lbs."
    //       value="lb"
    //       //   checked={value === "lb"}
    //       onChange={handleChange}
    //     />
    //     <Form.Radio
    //       label="Kgs."
    //       value="kg"
    //       //   checked={value === "kg"}
    //       onChange={handleChange}
    //     />
