import React, {useState} from 'react';
import logo from './logo.svg';
import './App.css';
import WeightCalculator from "./features/user/WeightCalendar"
import WeightChart from './features/user/WeightChart';

function App() {
  const [date, setDate] = useState(new Date())

  const handleChange = (event) => {
    console.log(event)
    setDate(event.target.value)
  }
  return (
    <div className="App">
      <WeightCalculator onChange={handleChange} value={date}/>
      <WeightChart />
    </div>
  );
}

export default App;
