import React, { Component } from "react";
import { Form, Dropdown, Button, Grid } from "semantic-ui-react";

const options = [
  { key: "f", text: "Fitness", value: "fitness" },
  { key: "s", text: "Self Love", value: "self love" },
  { key: "p", text: "Positive Affirmation", value: "positive affirmation" },
  { key: "i", text: "Inspirational", value: "inspirational" }
];

const Signup = () => {
  //   const { value } = this.state;

  const handleChange = event => {};

  return (
    <Grid centered>
      <Form>
        <Form.Input width={12} fluid label="Username" placeholder="Username" />
        <Form.Input width={6} fluid label="Email" placeholder="Email" />

        <Form.Input width={6} fluid label="Password" placeholder="Password" />
        <Form.Input
          width={6}
          fluid
          label="Confirm Password"
          placeholder="Email"
        />

        <Form.Group inline>
          <label>Weight Unit</label>
          <Form.Radio
            label="Lbs."
            value="lb"
            //   checked={value === "lb"}
            onChange={handleChange}
          />
          <Form.Radio
            label="Kgs."
            value="kg"
            //   checked={value === "kg"}
            onChange={handleChange}
          />
        </Form.Group>
        <div className="six wide field">
          <Dropdown
            width={6}
            placeholder="Quote Preference"
            fluid
            multiple
            selection
            options={options}
          />
        </div>
        <br />
        <Button animated="fade" color="purple">
          <Button.Content visible> Sign Up </Button.Content>
          <Button.Content hidden> Love Yourself </Button.Content>
        </Button>
      </Form>
    </Grid>
  );
};

export default Signup;
